import java.util.List;
import java.util.ArrayList;
import java.util.Map;
import java.io.File;
import java.nio.file.Files;
import java.nio.file.Paths;

public class App {
    public static void main(String[] args) {
        Pulumi.run(App::stack);
    }

    public static void stack(Context ctx) {
        var myapp = new Tenant("myapp", TenantArgs.builder()
            .accountName("myapp")
            .planId("nondefault")
            .build());

        var myfunction = new AwsLambdaFunction("myfunction", AwsLambdaFunctionArgs.builder()
            .tenantId(myapp.tenantId())
            .name("myfunction")
            .description("A description of my function")
            .runtime("java11")
            .handler("com.example.MyFunction::handleRequest")
            .s3Bucket("my-bucket-name")
            .s3Key("my-function.zip")
            .environment(AwsLambdaFunctionEnvironmentArgs.builder()
                .variables(Map.of("foo", "bar"))
                .build())
            .timeout(60)
            .memorySize(512)
            .build());

        var failureQueue = new AwsSqsQueue("failureQueue", AwsSqsQueueArgs.builder()
            .tenantId(myapp.tenantId())
            .name("failure_queue")
            .fifoQueue(false)
            .messageRetentionSeconds(345600)
            .visibilityTimeoutSeconds(30)
            .contentBasedDeduplication(false)
            .delaySeconds(10)
            .build());

        var successQueue = new AwsSqsQueue("successQueue", AwsSqsQueueArgs.builder()
            .tenantId(myapp.tenantId())
            .name("success_queue")
            .fifoQueue(false)
            .messageRetentionSeconds(345600)
            .visibilityTimeoutSeconds(30)
            .contentBasedDeduplication(false)
            .delaySeconds(10)
            .build());

        var event_invoke_config = new AwsLambdaFunctionEventConfig("event-invoke-config", AwsLambdaFunctionEventConfigArgs.builder()
            .tenantId(myapp.tenantId())
            .functionName(myfunction.fullname())
            .maxRetryAttempts(1)
            .maxEventAgeInSeconds(100)
            .destinationConfig(AwsLambdaFunctionEventConfigDestinationConfigArgs.builder()
                .onFailure(AwsLambdaFunctionEventConfigDestinationConfigOnFailureArgs.builder()
                    .destination(failureQueue.arn())
                    .build())
                .onSuccess(AwsLambdaFunctionEventConfigDestinationConfigOnSuccessArgs.builder()
                    .destination(successQueue.arn())
                    .build())
                .build())
            .build());

    }
}
