        final var tenant = DuplocloudFunctions.getTenant(GetTenantArgs.builder()
            .name("dev")
            .build());

        // Create a configmap with the env vars values 1. FOO: bar 2. PING: pong if it does not exists
        var nginx = new K8ConfigMap("nginx", K8ConfigMapArgs.builder()
            .tenantId(tenant.applyValue(getTenantResult -> getTenantResult.id()))
            .name("nginx-cm")
            .data(serializeJson(
                jsonObject(
                    jsonProperty("FOO", "bar"),
                    jsonProperty("PING", "pong")
                )))
            .build());

        // Ensure that the host is also created in the tenant.
        var nginxDuploService = new DuploService("nginxDuploService", DuploServiceArgs.builder()
            .tenantId(tenant.applyValue(getTenantResult -> getTenantResult.id()))
            .name("nginx")
            .agentPlatform(7)
            .dockerImage("nginx:latest")
            .replicas(1)
            .otherDockerConfig(nginx.name().applyValue(name -> serializeJson(
                jsonObject(
                    jsonProperty("EnvFrom", jsonArray(jsonObject(
                        jsonProperty("configMapRef", jsonObject(
                            jsonProperty("name", name)
                        ))
                    )))
                ))))
            .build());

    }
}
