import com.pulumi.duplocloud.TenantArgs;
import com.pulumi.random.password;
import com.pulumi.random.PasswordArgs;
import com.pulumi.duplocloud.RdsInstance;
import com.pulumi.duplocloud.RdsInstanceArgs;
import java.util.List;
import java.util.ArrayList;
import java.util.Map;
import java.io.File;
import java.nio.file.Files;
import java.nio.file.Paths;

public class App {
    public static void main(String[] args) {
        Pulumi.run(App::stack);
    }

    public static void stack(Context ctx) {
        // Before creating an RDS instance, you must first set up the infrastructure and tenant. Below is the resource for creating the infrastructure.
        var infra = new Infrastructure("infra", InfrastructureArgs.builder()
            .infraName("dev")
            .cloud(0)
            .region("us-east-1")
            .enableK8Cluster(false)
            .addressPrefix("10.13.0.0/16")
            .build());

        // Use the infrastructure name as the 'plan_id' from the 'duplocloud_infrastructure' resource while creating tenant.
        var tenant = new Tenant("tenant", TenantArgs.builder()
            .accountName("dev")
            .planId(infra.infraName())
            .build());

        // Generate a random password for the RDS instance.
        var password = new Password("password", PasswordArgs.builder()
            .length(16)
            .special(false)
            .build());

        // Create an RDS instance.
        var dev_db = new RdsInstance("dev-db", RdsInstanceArgs.builder()
            .tenantId(tenant.tenantId())
            .name("dev-db")
            .engine(1)
            .engineVersion("15.2")
            .size("db.t3.medium")
            .masterUsername("postgres")
            .masterPassword(password.result())
            .encryptStorage(true)
            .backupRetentionPeriod(7)
            .build());

    }
}
