import com.pulumi.duplocloud.S3Bucket;
import com.pulumi.duplocloud.S3BucketArgs;
import com.pulumi.duplocloud.inputs.S3BucketDefaultEncryptionArgs;
import java.util.List;
import java.util.ArrayList;
import java.util.Map;
import java.io.File;
import java.nio.file.Files;
import java.nio.file.Paths;

public class App {
    public static void main(String[] args) {
        Pulumi.run(App::stack);
    }

    public static void stack(Context ctx) {
        final var tenant = DuplocloudFunctions.getTenant(GetTenantArgs.builder()
            .name("qa")
            .build());

        var bucket = new S3Bucket("bucket", S3BucketArgs.builder()
            .tenantId(tenant.applyValue(getTenantResult -> getTenantResult.id()))
            .name("bucket")
            .allowPublicAccess(false)
            .enableAccessLogs(false)
            .enableVersioning(true)
            .managedPolicies("ssl")
            .defaultEncryption(S3BucketDefaultEncryptionArgs.builder()
                .method("Sse")
                .build())
            .build());

    }
}
